INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (162935, 'Belinostat', 'Rucaparib', 'Moderate', 'Coadministration with inhibitors of UGT1A1 may increase the plasma concentrations of belinostat, which is primarily (80% to 90%) metabolized by UGT1A1-mediated glucuronidation.', 'DDInter', 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided. Caution is advised when used with other agents that may also inhibit UGT1A1 such as nilotinib, pazopanib, regorafenib and sorafenib. Patients should be monitored closely for potentially increased adverse effects such as nausea, vomiting, diarrhea, thrombocytopenia, leukopenia (neutropenia and lymphopenia), anemia, infection, and hepatotoxicity.', 'Metabolism', 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163108/', '[1] "Product Information. Beleodaq (belinostat)." Spectrum Pharmaceuticals Inc, Irvine, CA.', 'Pertuzumab, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Porfimer sodium, Selinexor, More', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', 1767369485);
