INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (162990, 'Fostemsavir', 'Rucaparib', 'Moderate', 'Fostemsavir can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval. Consider obtaining electrocardiogram and serum electrolyte levels prior to initiating fostemsavir therapy and periodically thereafter, particularly in patients with risk factors for torsade de pointes and/or receiving one or more potent CYP450 3A4 inhibitors (e.g., azole antifungal agents; cobicistat; protease inhibitors; ketolide and certain macrolide antibiotics; ceritinib; idelalisib; nefazodone) or P-gp inhibitors (e.g., cyclosporine; diltiazem; quinidine; quinine; tacrolimus; verapamil). Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163163/', '[1] "Product Information. Rukobia (fostemsavir)." ViiV Healthcare, Research Triangle Park, NC.', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, More', 1767369485);
