INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (164915, 'Vemurafenib', 'Selexipag', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase the plasma concentrations of selexipag and its active metabolite. The interaction has not been studied; however, both selexipag and the metabolite undergo oxidative metabolism by CYP450 2C8 and 3A4.', 'DDInter', 'Caution is advised when selexipag is prescribed with CYP450 2C8 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring of selexipag should be considered whenever a CYP450 2C8 inhibitor is added to or withdrawn from therapy. Patients should be monitored for potentially increased side effects such as diarrhea, nausea, vomiting, flushing, anemia, and decreased appetite.', 'Metabolism', 'Caution is advised when selexipag is prescribed with CYP450 2C8 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165088/', '[1] "Product Information. Uptravi (selexipag)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[2] "Product Information. Viibryd (vilazodone)." Trovis Pharmaceuticals LLC, New Haven, CT.[3] "Product Information. Uptravi (selexipag)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.', 'Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, Ciclopirox, More', 'Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 1767369485);
