INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (169631, 'Teriflunomide', 'Temozolomide', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. The recent, concomitant, or sequential use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa. Due to the prolonged elimination half-life of leflunomide''s active metabolite, an interaction may occur even when these agents are initiated after the discontinuation of treatment with leflunomide or teriflunomide. Liver enzymes, bilirubin, platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy.', 'Synergism', 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169807/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, More', NULL, 1767369485);
