INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (169899, 'Tirofiban', 'Tenecteplase', 'Major', 'The intended and unintended effects of glycoprotein IIb/IIIa inhibitors and thrombolytic agents are additive, such that there is an increased frequency of major bleeding complications, including intracranial hemorrhage, retroperitoneal bleeding, spontaneous gastrointestinal (GI) and genitourinary (GU) bleeding among patients receiving both agents compared with the risk associated with the use of either agent alone.', 'DDInter', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications. The successful use of combination therapy with glycoprotein IIb/IIIa inhibitors and reduced doses of thrombolytics has been reported in clinical trials. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Thrombolytic and/or glycoprotein inhibitor therapy should be discontinued immediately if serious or uncontrollable bleeding occurs.', 'Synergism', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170075/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[4] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, N