INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (170000, 'Vinblastine', 'Teniposide', 'Minor', 'An in vitro test has shown teniposide to have inhibitory effects on the biotransformation of vinblastine. Both drugs are believed to be metabolized by the liver cytochrome P-450. The clinical significance of this interaction has not been established. If teniposide and vinblastine are used concomitantly, caution and close observation are advised.', 'DDInter', '-', 'Others', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170176/', '[1] Zhou-Pan XR, Seree E, Zhou XJ, et al "Involvement of human liver cytochrome P450 3A in vinblastine metabolism: drug interactions." Cancer Res 53 (1993): 5121-6', 'Irinotecan, Vinblastine', 'Teniposide, Irinotecan', 1767369485);