INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (177189, 'Grepafloxacin', 'Zolmitriptan', 'Moderate', 'Based on in vitro inhibition data, the coadministration with grepafloxacin may increase the plasma concentrations of drugs that are substrates of the CYP450 1A2 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 1A2 activity by grepafloxacin.', 'DDInter', 'Caution is advised if grepafloxacin must be used concomitantly with medications that undergo metabolism by CYP450 1A2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever grepafloxacin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if grepafloxacin must be used concomitantly with medications that undergo metabolism by CYP450 1A2, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/177400/', '[1] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.', 'Frovatriptan, Rimegepant, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Sumatriptan, Erenumab, Clonidine, Almotriptan, Fremanezumab', 'Enoxacin, Levofloxacin, Nalidixic acid, Gemifloxacin, Lomefloxacin, Moxifloxacin, Cinoxacin, Trovafloxacin, Delafloxacin, Sparfloxacin', 1767369485);
