INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (180976, 'Fosphenytoin', 'Everolimus', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the blood concentrations and pharmacologic effects of everolimus.', 'DDInter', 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered. Some manufacturers recommend that, if concomitant use is unavoidable, the daily everolimus dose should be doubled to achieve the recommended therapeutic range for the condition being treated. Please refer to the manufacturer''s labeling for specific dosing information. If the potent CYP450 3A4 and/or P-gp inducer is discontinued, the everolimus dosage should be returned to the dosage used before the potent inducer was commenced after a washout period of approximately 3 to 5 days. Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase, and 2 weeks after discontinuation of the potent inducers.', 'Metabolism', 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181190/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[5] "Product Information. Zortress (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, More', 'Futibatinib, Lenvatinib, Trametinib, Dacomitinib, Alectinib, Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Inebilizumab, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (180977, 'Fostamatinib', 'Everolimus', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', 'DDInter', 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181191/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 'Pralsetinib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Avapritinib, Selumetinib, Capmatinib, Ibrutinib, Duvelisib, Tivozanib, More', 'Eltrombopag, Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Phylloquinone, Lusutrombopag, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, More', 1767369485);
