INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184298, 'Fosphenytoin', 'Relugolix', 'Major', 'Coadministration with potent inducers of CYP450 3A4 that also induce P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of relugolix. In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8. Relugolix is also a substrate for intestinal P-gp.', 'DDInter', 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided. If coadministration is required, the manufacturer recommends increasing the relugolix dosage to 240 mg once daily. Therapeutic response and tolerance should be monitored more frequently. Following discontinuation of the P-gp/CYP450 3A inducer, treatment with relugolix should be resumed at the normally recommended dosage of 120 mg once daily.', 'Metabolism', 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184515/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc., Brisbane, CA.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 'Brivaracetam, Topiramate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Perampanel, More', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Ganirelix, Lanreotide, Gonadorelin, Anastrozole, Fulvestrant, Letrozole, Abarelix, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184299, 'Fosphenytoin', 'Riociguat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme.', 'DDInter', 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers. Patients should be closely monitored, and the dosage of riociguat adjusted as necessary. Data are not available to guide dosing of riociguat when coadministered with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Alternative agents with no or minimal CYP450 3A4-inducing activity should be considered whenever possible.', 'Metabolism', 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184516/', '[1] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.[2] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[3] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[4] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[5] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlorpropamide-alcohol flush." Eur J Clin Pharmacol 24 (1983): 237-42[6] Barnett AH, Spiliopoulos AJ, Pyke DA, et al "Metabolic studies in chlorpropamide-alcohol flush positive and negative type 2 (non-insulin dependent) diabetic patients with and without retinopathy." Diabetologia 24 