INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184549, 'Furosemide', 'Lomefloxacin', 'Minor', 'The plasma concentration of lomefloxacin following administration of furosemide has been reported to be higher than after lomefloxacin alone. The area under the curve of lomefloxacin was increased and the total and renal clearances of lomefloxacin were decreased. A similar interaction may occur if other quinolones are administered with other loop diuretics. The loop diuretic may decrease the renal clearance of the quinolone by competing for tubular secretion.', 'DDInter', '-', 'Excretion', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184766/', '[1] Sudoh T, Fujimura A, Shiga T, Sasaki M, Harada K, Tateishi T, Ohashi K, Ebihara A "Renal clearance of lomefloxacin is decreased by furosemide." Eur J Clin Pharmacol 46 (1994): 267-9', 'Bendroflumethiazide, Trichlormethiazide, Chlorthalidone, Metolazone, Hydrochlorothiazide, Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, More', 'Rifamycin, Sulfamethizole, Trifluridine, Benzylpenicillin, Sulfisoxazole, Levofloxacin, Povidone-iodine, Levofloxacin, Nalidixic acid, Gemifloxacin, Grepafloxacin, More', 1767369485);
