INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184614, 'Hydroxychloroquine', 'Gabapentin', 'Moderate', 'Chloroquine or hydroxychloroquine may lower the convulsive threshold, which may antagonize the action of antiepileptic medications. Seizures have been rarely reported in patients on chloroquine or hydroxychloroquine therapy. The mechanism is unknown.', 'DDInter', 'The potential risks and benefits should be evaluated prior to initiating chloroquine or hydroxychloroquine in patients on anticonvulsant therapy. In addition, patients with a history of epilepsy should be advised about the risk of chloroquine/hydroxychloroquine-induced seizures. Alternative anti-malarial agents may be required. Local treatment guidelines should be consulted.', 'Synergism', 'The potential risks and benefits should be evaluated prior to initiating chloroquine or hydroxychloroquine in patients on anticonvulsant therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184831/', '[1] "Product Information. Plaquenil (R). (hydroxychloroquine)." Sanofi Winthrop Pharmaceuticals, New York, NY.[2] "Product Information. Hydroxychloroquine Sulfate (hydroxychloroquine)." Prasco Laboratories, Cincinnati, OH.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Aralen (chloroquine)." Sanofi Winthrop Pharmaceuticals, New York, NY.[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[8] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[9] Gilman AG, Rall TW, Nies AS, Taylor P, e