INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184986, 'Irinotecan (liposomal)', 'Gemfibrozil', 'Major', 'Coadministration with strong inhibitors of CYP450 3A4 and/or UGT1A1 may increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 and UGT1A1 are the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'DDInter', 'Concomitant use of irinotecan with strong inhibitors of CYP450 3A4 and/or UGT1A1 should generally be avoided. If coadministration is required, patients should be monitored for toxicities such as diarrhea, myelosuppression, thromboembolism, and interstitial lung disease, and the irinotecan dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Concomitant use of irinotecan with strong inhibitors of CYP450 3A4 and/or UGT1A1 should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185203/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals, Cambridge, MA.[4] "Product Information. Camptosar (irinotecan)." Pharmacia and Upjohn, Kalamazoo, MI.[5] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[6] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[7] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman S "Single-dose pharmacokinetics of indinavir and the effect of food." Antimicrob Agents Chemother 42 (1998): 332-8', 'Vincristine, Cabazitaxel, Vinblastine, Topotecan, Teniposide, Vinorelbine, Etoposide', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Ezetimibe, Niacin, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184987, 'Gemfibrozil', 'Letermovir', 'Moderate', 'Coadministration with gemfibrozil may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 transporter. The mechanism is decreased clearance due to inhibition of OATP 1B1-mediated hepatic uptake by gemfibrozil and its metabolite, gemfibrozil 1-O-beta-glucuronide. The clinical relevance of this interaction is unknown.', 'DDInter', 'A dosage reduction of OATP 1B1 substrates may be required when used with gemfibrozil. Clinical and laboratory monitoring may be appropriate for some drugs whenever gemfibrozil is added to or withdrawn from therapy.', 'Excretion', 'A dosage reduction of OATP 1B1 substrates may be required when used with gemfibrozil.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185204/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Lasix (furosemide)." sanofi-aventis , Bridgewater, NJ.', 'Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, Niacin, More', 'Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184988, 'Gemfibrozil', 'Lonafarnib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1. Increased exposure to lonafarnib may increase the risk and/or severity of adverse effects such as nausea, vomiting, diarrhea, anorexia, electrolyte disturbances, liver enzyme elevations, myelosuppression, infection, and hypertension.', 'DDInter', 'The manufacturer recommends avoiding concomitant use of lonafarnib with CYP450 2C9 inhibitors. If coadministration is required, patients should be closely monitored for common adverse effects as well as arrhythmias and events such as syncope and heart palpitations, since the effect of increased exposures on the QT interval is unknown.', 'Metabolism', 'The manufacturer recommends avoiding concomitant use of lonafarnib with CYP450 2C9 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185205/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Rosuvastatin, Clofibrate, Colestipol, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 1767369485);
