INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188237, 'Indomethacin', 'Diflunisal', 'Major', 'Concomitant administration of diflunisal may increase serum indomethacin concentrations by up to two- to threefold and increase the risk of toxicity such as gastrointestinal and central nervous system side effects. The mechanism is unknown, but may involve competition for the same metabolic and secretory pathways, and/or inhibition of glucuronidation by diflunisal. The renal clearance of indomethacin and its conjugate are also significantly reduced. Fatal gastrointestinal hemorrhage has been reported.', 'DDInter', 'The manufacturer recommends that this combination should be avoided.', 'Metabolism, Excretion', 'The manufacturer recommends that this combination should be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188461/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Eriksson LO, Wahlin-Boll E, Liedholm H, et al "Influence of chronic diflunisal treatment on the plasma levels, metabolism and excretion of indomethacin." Eur J Clin Pharmacol 37 (1989): 7-15[3] "Product Information. Dolobid (diflunisal)." Merck & Co, Inc, West Point, PA.[4] Van Hecken A, Verbesselt R, Tjandra-Maga TB, De Schepper PJ "Pharmacokinetic interaction between indomethacin and diflunisal." Eur J Clin Pharmacol 36 (1989): 507-12[5] Cerner Multum, Inc. "Australian Product Information." O 0', 'Fluocinolone acetonide, Fluorometholone, Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Fluocinolone acetonide, Salicylic acid, Fluorometholone, Tolazoline, More', 'Salicylic acid, Choline salicylate, Ziconotide, Salsalate', 1767369485);
