INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188373, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Polatuzumab vedotin', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', 'DDInter', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188597/', '[1] "Product Information. Adenovirus Vaccine Type 4 and Type 7 (adenovirus vaccine, live)." Teva Pharmaceuticals USA, North Wales, PA.', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188374, 'Amprenavir', 'Polatuzumab vedotin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. Polatuzumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', 'DDInter', 'Caution is advised when polatuzumab vedotin is used concomitantly with potent CYP450 3A4 inhibitors. Patients should be closely monitored for development or exacerbation of toxicities such as peripheral neuropathy, myelosuppression, opportunistic infections, tumor lysis syndrome and hepatotoxicity, and the dosing of polatuzumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when polatuzumab vedotin is used concomitantly with potent CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188598/', '[1] "Product Information. Polivy (polatuzumab vedotin)." Genentech, South San Francisco, CA.', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
