INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188825, 'Amoxapine', 'Dextrothyroxine', 'Moderate', 'Coadministration of thyroid hormone replacement therapy with tricyclic antidepressants may accelerate the onset or potentiate the action of tricyclic antidepressants, increasing the risk of cardiac arrhythmias and CNS stimulation. The proposed mechanism may be an increased receptor sensitivity to catecholamines. Some clinicians have used this interaction therapeutically.', 'DDInter', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation. Advise patients to contact their doctor if they experience toxicity symptoms such as: anxiety, agitation, insomnia, shortness of breath, irregular or fast heartbeat, and lightheadedness or dizziness.', 'Synergism', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189049/', '[1] "Product Information. Triostat (liothyronine)." JHP Pharmaceuticals, Saddle River, NJ.[2] Goodwin FK, Prange AJ Jr, Post RM, Muscettola G, Lipton MA "Potentiation of antidepressant effects by L-triiodothyronine in tricyclic nonresponders." Am J Psychiatry 139 (1982): 34-8[3] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[4] Prange AJ, Wilson IC, Rabon AM, Lipton MA "Enhancement of imipramine antidepressant activity by thyroid hormone." Am J Psychiatry 126 (1969): 457-69[5] "Product Information. Anafranil (clomipramine)." Basel Pharmaceuticals, Summit, NJ.[6] Extein IL, Gold MS "T