INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (192007, 'Budesonide (nasal)', 'Lonafarnib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the systemic bioavailability of budesonide, which undergoes extensive first-pass and systemic metabolism via intestinal and hepatic CYP450 3A4. In pharmacokinetic studies, 6- to 8-fold increases in budesonide systemic exposure (AUC) have been observed during coadministration of the potent CYP450 3A4 inhibitor ketoconazole with different oral formulations of budesonide.', 'DDInter', 'The possibility of increased systemic adverse effects of budesonide should be considered during coadministration with CYP450 3A4 inhibitors. If concomitant use cannot be avoided, the dosing times between budesonide and the CYP450 3A4 inhibitor should be separated by as much as possible. In addition, the lowest effective dosage of budesonide should be prescribed, and further adjustments made as necessary according to therapeutic response and tolerance.', 'Metabolism', 'The possibility of increased systemic adverse effects of budesonide should be considered during coadministration with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192235/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Bolland MJ, Bagg W, Thomas MG, Lucas JA, Ticehurst R, Black PN "Cushing''s syndrome due to interaction between inhaled corticosteroids and itraconazole." Ann Pharmacother 38 (2004): 46-9[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Daveluy A, Raignoux C, Miremont-Salame G, et al. "Drug interactions between inhaled corticosteroids and enzymatic inhibitors." Eur J Clin Pharmacol (2009):[5] "Product Information. Entocort (budesonide)."