INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (192026, 'Hydroxychloroquine', 'Lonafarnib', 'Moderate', 'Coadministration with moderate or strong inhibitors of CYP450 2C8 or CYP450 3A4 may increase the plasma concentration of hydroxychloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.', 'DDInter', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor. Clinical and laboratory monitoring should be considered whenever a moderate or strong CYP450 3A4 or 2C8 inhibitor is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192254/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0', 'Lumefantrine, Pyrimethamine, Tafenoquine, Atovaquone, Primaquine, Proguanil, Sulfadoxine', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (192027, 'Lonafarnib', 'Lacosamide', 'Moderate', 'Coadministration of strong inhibitors of CYP450 3A4 and/or CYP450 2C9 in patients with renal or hepatic impairment may significantly increase lacosamide plasma concentrations and increase the risk of lacosamide toxicity. The presumed mechanism is additive reduction in lacosamide clearance.', 'DDInter', 'For patients with renal and/or hepatic impairment concomitantly receiving a strong CYP450 3A4 and/or CYP450 2C9 inhibitor, lacosamide dose reductions in addition to those recommended for renal and/or hepatic impairment may be necessary. Manufacturer labeling should be consulted for dose recommendations in renal and/or hepatic impairment. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat. Lacosamide dose adjustments may be necessary whenever a strong CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy in patients with renal and/or hepatic impairment.', 'Metabolism', 'For patients with renal and/or hepatic impairment concomitantly receiving a strong CYP450 3A4 and/or CYP450 2C9 inhibitor, lacosamide dose reductions in addition to those recommended for renal and/or hepatic impairment may be necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192255/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Vimpat (lacosamide)." UCB Pharma Inc, Smyrna, GA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 'Ethotoin, Brivaracetam, Gabapentin, Topiramate, Ezogabine, Paramethadione, Perampa