INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (192234, 'Drotrecogin alfa', 'Meclofenamic acid', 'Major', 'Coadministration of drotrecogin alfa and other drugs that interfere with coagulation or platelet function may potentiate the risk of bleeding complications. Drotrecogin alfa inactivates blood clotting factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT).', 'DDInter', 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia. Close clinical and laboratory monitoring for bleeding complications is recommended if concurrent therapy is required. Drotrecogin alfa should be discontinued immediately if clinically significant bleeding occurs.', 'Synergism', 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192462/', '[1] "Product Information. Xigris (drotrecogin alfa)." Lilly, Eli and Company, Indianapolis, IN.', 'Protein C, Acenocoumarol, Selexipag', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib, Tolazoline, Rabeprazole, Dimethyl sulfoxide', 1767369485);
