INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (192247, 'Phenylbutazone', 'Meclofenamic acid', 'Moderate', 'Concomitant use of more than one nonsteroidal anti-inflammatory drug (NSAID) at a time may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation of the esophagus, stomach, or intestines. These events can occur at any time during NSAID use, with or without warning symptoms. The risk is dependent on both dosage and duration of therapy.', 'DDInter', 'Concomitant use of more than one NSAID at a time should generally be avoided. Patients treated with an NSAID should be advised to take it with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, misoprostol, proton pump inhibitors) may be considered in high risk patients.', 'Synergism', 'Concomitant use of more than one NSAID at a time should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192475/', '[1] "Product Information. Duexis (famotidine-ibuprofen)." Horizon Pharma USA Inc, Northbrook, IL.[2] "Product Information. Mobic (meloxicam)" Boehringer-Ingelheim, Ridgefield, CT.[3] "Product Information. Ponstel (mefenamic acid)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] "Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc, Palo Alto, CA.[5] "Product Information. VIMOVO (esomeprazole-naproxen)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[6] "Product Information. Caldolor (ibuprofen)." Cumberland Pharmaceuticals Inc, Nashville, TN.[7] "Product Information. Meclofenamate Sodium (meclofenamate)." Mylan Pharmaceuticals Inc, Morgantown, WV.[8] "Product Information. Lodine (etodolac)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[9] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[10] "Product Information. Anaprox (naproxen)." Roche Laboratories, Nutley, NJ.[11] "Product Information. Nalfon (fenoprofen)." Xspire Pharma, Ridgeland, MS.[12] "Product Information. Etodolac ER (etodolac)." Taro Pharmaceuticals U.S.A. Inc, Hawthorne, NY.[13] "Product Information. Cambia (diclofenac)." Kowa Pharmaceuticals America (formerly ProEthic), Montgomery, AL.[14] "Product Information. Ketoprofen ER (ketoprofen)." Mylan Pharmaceuticals Inc, Morgantown, WV.[15] "Product Information. Indocin (indomethacin)." Merck & Co, Inc, West Point, PA.', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Esomeprazole, Meclofenamic acid, Tolazoline, Rabeprazole, Meclofenamic acid, Dimethyl sulfoxide', 'Rabeprazole, Phenylbutazone, Misoprostol, Glucosamine, Chondroitin sulfate, Esomeprazole, Tolazoline, Rabeprazole, Dimethyl sulfoxide, Phenylbutazone', 1767369485);
