INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237351, 'Chlorpheniramine', 'Botulinum toxin type A', 'Moderate', 'Use of anticholinergic drugs after administration of botulinum toxin may potentiate systemic anticholinergic effects such as dry mouth, blurred vision, and urinary disorders.  Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive or synergistic anticholinergic effects may occur when these agents are used together.', 'DDInter', 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) are used after administration of botulinum toxin.', 'Synergism', 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243030/', '[1] "Product Information. Myobloc (rimabotulinumtoxinB)." Elan Pharmaceuticals[2] "Product Information. Dysport (abobotulinumtoxinA)." Tercica Inc[3] "Product Information. Botox (onabotulinumtoxinA)." Allergan Inc[4] "Product Information. Xeomin (incobotulinumtoxinA)." Merz Pharmaceuticals  (2022):[5] "Product Information. Jeuveau (prabotulinumtoxinA)." Evolus, Inc.  (2022):[6] "Product Information. Daxxify (daxibotulinumtoxinA)." Revance Therapeutics, Inc.  (2022):', 'Ketotifen, Levocetirizine, Cetirizine, Fexofenadine, Loratadine, Trimethobenzamide, Desloratadine, Azelastine, Terfenadine, Astemizole',