INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237395, 'Darolutamide', 'Palonosetron', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.  Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243166/', '[1] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc  (2002):[2] "Product Information. Zoladex (goserelin)." Astra-Zeneca Pharmaceuticals  (2001):[3] "Product Information. Trelstar (triptorelin)." Pharmacia and Upjohn  (2001):[4] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals  (2002):[5] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc  (2003):[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc)  (2010):[8] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc  (2013):', 'Anastrozole, Aminoglutethimide, Exemestane, Letrozole', 'Nabilone, Scopolamine, Rolapitant, Dola