INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237502, 'Aluminum hydroxide', 'Carteolol', 'Minor', 'Concurrent administration with aluminum and magnesium antacids has been shown to decrease the oral bioavailability of certain beta-blockers, although data are conflicting.  The exact mechanism of interaction is unknown but may involve cation binding of beta-blockers or a reduction in the dissolution rate due to increased gastric pH.', 'DDInter', 'Based on available data, the clinical significance of this potential interaction is difficult to determine.  As a precaution, patients may want to consider separating the administration times of beta-blockers and antacids or other aluminum- or magnesium-containing products by at least 2 hours.', 'Absorption', 'Based on available data, the clinical significance of this potential interaction is difficult to determine.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243720/', '[1] Kirch W, Schafer-Korting M, Axthelm T, Kohler H, Mutschler E "Interaction of atenolol with furosemide and calcium and aluminum salts." Clin Pharmacol Ther 30 (1981):  429-35[2] D''Arcy PF, McElnay JC "Drug-antacid interactions: assessment of clinical importance." Drug Intell Clin Pharm 21 (1987):  607-17[3] Hong CY, Hu SC, Lin SJ, Chiang BN "Lack of influence of aluminum hydroxide on the bioavailability and beta-adrenoceptor blocking activity of propranolol." Int J Clin Pharmacol Ther Toxicol 23 (1985):  244-6[4] Dobbs JH, Skoutakis VA, Acchiardo SR, Dobbs BR "Effects of aluminum hydroxide on the absorption of propranolol." Curr Ther Res Clin Exp 21 (1977):  887-92[5] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol."