INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237543, 'Diclofenac (topical)', 'Diclofenac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs may be absorbed systemically following topical administration.  Concomitant use with systemic nonsteroidal anti-inflammatory drugs may result in increased adverse effects such as gastrointestinal toxicity (inflammation, bleeding, ulceration, perforation), cardiovascular thrombotic events, hepatotoxicity (liver enzyme elevations), renal toxicity, fluid retention, edema, hypertension, and inhibition of platelet aggregation.', 'DDInter', 'Patients treated with topical preparations of nonsteroidal anti-inflammatory drugs should avoid or limit the use of systemic nonsteroidal anti-inflammatory drugs.  Although systemic exposure is generally low following topical administration, absorption may be increased with frequent applications; use of large quantities or over large areas of skin; use on compromised or diseased skin or open wounds; and use of occlusive dressings or heating pads over application areas.', 'Synergism', 'Patients treated with topical preparations of nonsteroidal anti-inflammatory drugs should avoid or limit the use of systemic nonsteroidal anti-inflammatory drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/244001/', '[1] "Product Information. Solaraze (diclofenac topical)." Doak Dermatologics Division  (2009):', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, Fluocinolone acetonide, Cortisone, More', 'Fluorometholone, Fluocinolone acetonide, Diclofenac, Fluorometholone, Fluocinolone acetonide, Salicylic acid, Diclofenac, Glucosamine, Chondroitin sulfate, Esom