INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237612, 'Fidaxomicin', 'Pazopanib', 'Moderate', 'Coadministration with fidaxomicin may increase the plasma concentrations of some orally administered drugs that are substrates of the P-glycoprotein (P-gp) transporter.  In vitro, fidaxomicin and its main metabolite are substrates and inhibitors of P-gp, an efflux transporter expressed in the gastrointestinal tract.', 'DDInter', 'Caution is advised when fidaxomicin is used in combination with P-gp substrates that may be sensitive to intestinal P-gp inhibition.', 'Absorption', 'Caution is advised when fidaxomicin is used in combination with P-gp substrates that may be sensitive to intestinal P-gp inhibition.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245368/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Dificid (fidaxomicin)." Optimer Pharmaceuticals  (2011):[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Rifaximin, Paromomycin, Neomycin', 'Entrectinib, Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Fedratinib, Avapritinib, Selumetinib, Ribociclib, Vandetanib, Duvelisib, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237613, 'Gefitinib', 'Pazopanib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of gefitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if gefitinib is administered with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.  Pharmacologic response to gefitinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.  Patients should be advised to contact their physician if they experience possible symptoms of gefitinib toxicity such as severe diarrhea, nausea, dyspnea, cough, and fever.', 'Metabolism', 'Caution is advised if gefitinib is administered with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245372/', '[1] "Product Information. Iressa (gefitinib)." Astra-Zeneca Pharmaceuticals  (2003):[2] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007):  3731-7', 'Regorafenib, Copanlisib, Abemaciclib, Erlotinib, Ponatinib, Neratinib, Erdafitinib, Trametinib, Nintedanib, Afatinib, Dacomitinib, More', 'Acalabrutinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibru