INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237614, 'Pazopanib', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', 'DDInter', 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil).  Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine).  Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245386/', '[1] "Product Information. Chirocaine (levobupivacaine)." Organon  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 'Lidocaine, Chloroprocaine, Bupivacaine, Tetracaine, Mepivacaine, Meloxicam, Procaine, Prilocaine, Articaine, Etidocaine', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Ibrutinib, Afatinib, Tivozanib, More', 1767369485);
