INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237836, 'Sodium oxybate', 'Scopolamine', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects.', 'DDInter', 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.  Otherwise, close monitoring and/or dosage reductions should be considered.  If short-term use of a CNS depressant is required (e.g., post- or perioperative opioid), a temporary interruption of sodium oxybate therapy may be appropriate.  All patients treated with sodium oxybate should be advised not to drive, operate machinery, or engage in potentially hazardous activities requiring mental alertness and motor coordination for at least 6 hours after taking the second nightly dose of sodium oxybate and until they know how the medication affects them.', 'Synergism', 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246048/', '[1] "Product Information. Xyrem (sodium oxybate)." Orphan Medical  (2002):[2] "Product Information. Xywav (calcium/magnesium/potass/sodium oxybates)." Jazz Pharmaceuticals  (2020):', 'Amobarbital, Melatonin, Nitrazepam, Zopiclone, Ketorolac, Homatropine, Atropine, Ephedrine, Methscopolamine, Tropicamide, Phenylephrine, More', 'Halothane, Isoflurane, Nitrous oxide, Desflurane, Enflurane, Sevoflurane, Etomidate, Dalfampridine, Riluzole, Inotersen, Pitolisant, More', 1767369485);
