INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (237907, 'Niraparib', 'Melphalan flufenamide', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with niraparib.', 'DDInter', 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.  Complete blood counts should be performed weekly for the first month, monthly for the next 11 months, and periodically thereafter.  Do not start niraparib until patients have recovered from hematological toxicity caused by previous chemotherapy (Grade 1 or less).  If hematological toxicities have not resolved within 4 weeks after interruption, discontinue niraparib and refer the patient to a hematologist for further investigations, including bone marrow analysis and blood sample for cytogenetics.  If MDS/AML is confirmed, discontinue niraparib.  Patients should be advised to contact their physician if they experience weakness, fatigue, fever, weight loss, infections, shortness of breath, unusual bleeding or bruising, or blood in urine or stool.', 'Synergism', 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246387/', '[1] "Product Information. Zejula (niraparib)." Tesaro Inc.  (2017):', 'Pertuzumab, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, More', NULL, 1767369485);
