INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (238490, 'Dexmethylphenidate', 'Nitroprusside', 'Moderate', 'Methylphenidate and other derivative amphetamines may decrease the therapeutic effects of antihypertensive drugs.', 'DDInter', 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.  The dosage of the antihypertensive drug should be adjusted as needed.', 'Antagonism', 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/248962/', '[1] "Product Information. Ritalin (methylphenidate)." Novartis Pharmaceuticals  (2001):[2] "Product Information. Metadate CD (methylphenidate)." Celltech Pharmaceuticals Inc  (2022):[3] "Product Information. Focalin (dexmethylphenidate)." Mikart Inc  (2001):[4] "Product Information. Metadate ER (methylphenidate)." Celltech Pharmaceuticals Inc  (2002):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Ritalin LA (methylphenidate)." Quality Care Products/Lake Erie Medical