INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239000, 'Dihydroergotamine', 'Bupropion', 'Moderate', 'Translated with DeepL', 'DDInter', 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.  Blood pressure and heart rate should be measured prior to initiating bupropion therapy and monitored at regular intervals consistent with usual clinical practice, particularly in patients with preexisting hypertension.  Dose reduction or discontinuation of bupropion should be considered in patients who experience clinically significant and sustained increases in blood pressure or heart rate.', 'Synergism', 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251419/', '[1] "Product Information. Zyban (bupropion)." GlaxoSmithKline UK Ltd  (2022):[2] "Product Information. Auvelity (bupropion-dextromethorphan)." Axsome Therapeutics, Inc.  (2022):[3] "Product Information. Wellbutrin XL (bupropion)." Bausch Health, Canada Inc.  (2022):[4] "Product Information. Contrave (bupropion-naltrexone)." Currax Pharmaceuticals LLC  (2021):', 'Naltrexone, Phentermine, Orlistat, Diethylpropion, Mazindol, Esketamine', 'Frovatriptan, Eptinezumab, Rimegepant, Ubrogepant, Galcanezumab, Atogepant, Erenumab, Almotriptan, Fremanezumab', 1767369485);