INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239456, 'Dexmethylphenidate', 'Atenolol', 'Moderate', 'Methylphenidate and other derivative amphetamines may decrease the therapeutic effects of antihypertensive drugs.', 'DDInter', 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.  The dosage of the antihypertensive drug should be adjusted as needed.', 'Antagonism', 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/254747/', '[1] "Product Information. Ritalin (methylphenidate)." Novartis Pharmaceuticals  (2001):[2] "Product Information. Metadate CD (methylphenidate)." Celltech Pharmaceuticals Inc  (2022):[3] "Product Information. Focalin (dexmethylphenidate)." Mikart Inc  (2001):[4] "Product Information. Metadate ER (methylphenidate)." Celltech Pharmaceuticals Inc  (2002):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Ritalin LA (methylphenidate)." Quality Care Products/Lake Erie Medical  (2013):[8] "Product Information. Azstarys (dexmethylphenidate-serdexmethylphenidate)." Corium, Inc.  (2021):', 'Acetylsalicylic acid, Ivabradine', 'Amphetamine, Pemoline, Metamfetamine, Lisdexamfetamine, Solriamfetol, Caffeine, Dextroamphetamine, Armodafinil, Citicoline', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239457, 'Pyrophosphoric acid', 'Atenolol', 'Moderate', 'Medicinal products including sodium heparin, prazosin, methyldopa, hydralazine, quinidine, beta blockers, calcium channel blockers, digitalis glycosides, nitrates, anthracyclines, and iodinated contrast agents may interfere with the blood pool images taken using Technetium Tc 99m pyrophosphate.  This is characterized by a reduction in the amount of injected radioactivity remaining in the blood pool.', 'DDInter', 'Clinicians should be aware of the possibility of a diagnostic interference.  When use of iodinated contrast media is anticipated, it is recommended to perform the scintigraphy with (99mTc)-labeled red blood cells prior to administration of the iodinated contrast media to avoid adversely affecting the red blood cell labeling.', 'Distribution', 'Clinicians should be aware of the possibility of a diagnostic interference.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/254765/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Pyrophosphate (pyrophosphate)." Pharmalucence Inc  (2012):', 'Carteolol, Acetylsalicylic acid, Ivabradine', NULL, 1767369485);
