INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239595, 'Dexamethasone', 'Axicabtagene ciloleucel', 'Major', 'Prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy may interfere with the therapeutic effects of such drugs as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel.  The mechanism for this interaction has not been delineated.  Administration of systemic corticosteroids in an effort to manage the toxicities associated with these drugs does not affect the expansion and persistence of CAR T cells.', 'DDInter', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.  However, systemic corticosteroids may be used for the management of adverse effects associated with these drugs, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of CAR T cells.', 'Others', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256579/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Yescarta (axicabtagene ciloleucel)." Kite Pharma, Inc.  (2017):[3] "Product Information. Tecartus (brexucabtagene autoleucel)." Kite Pharma, Inc.  (2020):[4] "Product Information. Breyanzi (lisocabtagene maraleucel)." Bristol-Myers Squibb  (2021):[5] "Product Information. Abecma (idecabtagene vicleucel)." Celgene Corporation  (2021):', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Niraparib, Porfimer sodium, Selinexor, More', 'Mupirocin, Mometasone furoate, Epinephrine, Vitamin A, Ephedrine, Olopatadine, Ipratropium, Cromoglicic acid, Hyaluronic acid, Nedocromil, Phenylephrine, More', 1767369485);
