INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239704, 'Anisindione', 'Valdecoxib', 'Moderate', 'Coadministration with valdecoxib may increase the plasma concentrations and hypoprothrombinemic effect of warfarin and other oral anticoagulants.  The exact mechanism of interaction is unknown, although it is possible that valdecoxib partially inhibits the metabolic clearance of warfarin via CYP450 2C9 and 2C19.', 'DDInter', 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and valdecoxib or parecoxib.  The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of valdecoxib in patients who are stabilized on their anticoagulant regimen.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism', 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and valdecoxib or parecoxib.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258050/', '[1] "Product Information. Bextra (valdecoxib)." Pharmacia and Upjohn  (2001):', 'Misoprostol, Phenylbutazone', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239705, 'Anistreplase', 'Valdecoxib', 'Moderate', 'Anticoagulants, antiplatelet agents, and thrombolytics may potentiate the risk of gastrointestinal bleeding complications associated with the use of nonsteroidal anti-inflammatory drugs (NSAIDs) including COX-2 inhibitors.', 'DDInter', 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.  Gastroprotective agents such as H2-receptor antagonists or proton pump inhibitors may be appropriate in high risk patients.', 'Synergism', 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258051/', '[1] "Product Information. Celebrex (celecoxib)." Searle  (2001):', 'Drotrecogin alfa, Acenocoumarol, Selexipag, Desirudin', 'Rabeprazole, Phenylbutazone, Misoprostol, Glucosamine, Chondroitin sulfate, Esomeprazole', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239706, 'Valdecoxib', 'Bendroflumethiazide', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states.  The risk may be increased in patients on dietary sodium restriction.  At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure.  Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination.', 'DDInter', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.  If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Synergism', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258053/', '[1] Allan SG, Knox J, Kerr F "Interaction between diuretics and indomethacin." Br Med J 283 (1981):  1611[2] McCarthy JT, Torres VE, Romero JC, et al. "Acute intrinsic renal failure induced by indomethacin." Mayo Clin Proc 57 (1982):  289-96[3] Favre L, Glasson P, Vallotton MB "Reversible acute renal failure from combined triamterene and indomethacin." Ann Intern 