INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239708, 'Boceprevir', 'Valdecoxib', 'Moderate', 'Coadministration with boceprevir may increase the plasma concentrations of drugs that are metabolized by CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by boceprevir.', 'DDInter', 'Caution is advised if boceprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever boceprevir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if boceprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258056/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):', 'Paritaprevir, Glecaprevir, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Cobicistat, More', 'Rabeprazole, Ketoprofen, Diclofenac, Piroxicam, Rofecoxib, Sulindac, Meclofenamic acid, Etodolac, Ketorolac, Misoprostol, Glucosamine, More', 1767369485);
