INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239739, 'Bromocriptine', 'Bicalutamide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of bromocriptine.  Orally administered bromocriptine is extensively metabolized in the gastrointestinal tract and liver by CYP450 3A4, with approximately 93% of the absorbed dose undergoing first-pass metabolism and only the remaining 7% reaching systemic circulation.  As such, inhibitors of CYP450 3A4 may markedly reduce the metabolic clearance of bromocriptine.', 'DDInter', 'Caution and close monitoring for development of adverse effects are advisable during coadministration of bromocriptine with CYP450 3A4 inhibitors, and the bromocriptine dosage adjusted as necessary.', 'Metabolism', 'Caution and close monitoring for development of adverse effects are advisable during coadministration of bromocriptine with CYP450 3A4 inhibitors, and the bromocriptine dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258211/', '[1] Nelson MV, Berchou RC, Kareti D, Le Witt PA "Pharmacokinetic evaluation of erythromycin and caffeine administered with bromocriptine." Clin Pharmacol Ther 47 (1990):  694-7[2] "Product Information. Parlodel (bromocriptine)." Sandoz Pharmaceuticals Corporation  (2001):[3] von Rosenstiel NA, Adam D "Macrolide antibacterials. Drug interactions of clinical significance." Drug Saf 13 (1995):  105-22[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Periti P, Mazzei T, Mini E, Novelli A "Pharmacokinetic drug interactions of macrolides." Clin Pharmacokinet 23 (1992):  106-31[7] "Product Information. Cycloset (bromocriptine)." Valeant Pharmaceuticals  (2018):', 'Darolutamide, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Abiraterone, More', 'Ropinirole, Opicapone, Cabergoline, Amantadine, Rasagiline, Entacapone, Selegiline, Tolcapone, Rotigotine, Pergolide, Pramipexole, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239740, 'Drospirenone', 'Bicalutamide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of drospirenone, which is partially metabolized by the isoenzyme.', 'DDInter', 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.g., renal insufficiency; coadministration with medications that can increase potassium such as aldosterone antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium-sparing diuretics, potassium supplements, and heparin).  Consider checking serum potassium levels during the first treatment cycle and periodically thereafter as indicated.', 'Metabolism', 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258214/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Slynd (drospirenone)." Exeltis USA, Inc.  (2022):[3] "Product Information. Angeliq (drospirenone-estradiol)." Bayer Pharmaceutical Inc  (2017):[4] "Product Information. Slinda (drospirenone)." Besins Healthcare Australia Pty Ltd  (2021):[5] "Product Information. Angeliq 1/2 (drospirenone-estradiol)." Bayer Australia Limited  (2021):[6] "Product Information. Slynd (drospirenone)." Duchesnay Inc  (20