INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (239741, 'Fedratinib', 'Bicalutamide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fedratinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19 and flavin-containing monooxygenase 3 (FMO3).', 'DDInter', 'Caution is advised when fedratinib is used with CYP450 3A4 inhibitors, particularly those that may also inhibit CYP450 2C19 (e.g., amiodarone, cimetidine, fexinidazole, isoniazid, osilodrostat, rucaparib) or if combined with other drugs that can inhibit CYP450 2C19 (e.g., esomeprazole, fenofibrate, fluoxetine, lansoprazole, letrozole, moclobemide, omeprazole, parecoxib, ticlopidine).  Patients should be monitored for adverse reactions such as nausea, vomiting, diarrhea, anemia, thrombocytopenia, neutropenia, encephalopathy (including Wernicke''s), liver (ALT, AST) and pancreatic (amylase, lipase) enzyme elevations, increased blood creatinine, and secondary malignancies.  Dosage adjustment or treatment interruption may be required based on patient tolerance and response in accordance with the prescribing information.', 'Metabolism', 'Caution is advised when fedratinib is used with CYP450 3A4 inhibitors, particularly those that may also inhibit CYP450 2C19 (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258215/', '[1] Wu F, Krishna G, Surapaneni S "Physiologically based pharmacokinetic modeling to assess metabolic drug-drug interaction risks and inform the drug label for fedratinib." Cancer Chemother Pharmacol 86 (2020):  461-73[2] "Product Information. Inrebic (fedratinib)." Bristol-Myers Squibb  (2022):[3] "Product Information. Inrebic (fedratinib)." Bristol-Myers Squibb Pharmaceut