INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241626, 'Digitoxin', 'Tacrine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.  Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block.  Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/268522/', '[1] "Product Information. Cognex (tacrine)." Parke-Davis  (2001):[2] "Product Information. Aricept (donepezil)." Pfizer U.S. Pharmaceuticals  (2001):[3] "Product Information. Exelon (rivastigmine)." Novartis Pharmaceuticals  (2001):[4] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc  (2005):[5] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007):  1063-71[6] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998):  40-4', 'Memantine, Ginkgo biloba, Aducanumab', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (241627, 'Edrophonium', 'Tacrine', 'Moderate', 'A synergistic effect may be expected when acetylcholinesterase inhibitors are administered concurrently with other cholinesterase inhibitors or cholinergic agonists.', 'DDInter', 'Monitoring for excessive cholinergic effects and seizure activity is recommended.  Patients should be advised to notify their physician if they experience nausea, vomiting, diarrhea, abdominal pain, increased salivation, cold sweats, slow or irregular heartbeat, muscle weakness or cramps, dizziness, headache, seizures, or respiratory distress.', 'Synergism', 'Monitoring for excessive cholinergic effects and seizure activity is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/268523/', '[1] "Product Information. Cognex (tacrine)." Parke-Davis  (2001):[2] "Product Information. Aricept (donepezil)." Pfizer U.S. Pharmaceuticals  (2001):[3] "Product Information. Reminyl (galantamine)." Janssen Pharmaceuticals  (2001):', 'Sorbitol, Caffeine, Indocyanine green, Tolbutamide, Inulin, Hexaminolevulinate, Secretin human, Galactose, Corticorelin, Methacholine, Folic acid, More', 'Memantine, Ginkgo biloba, Aducanumab', 1767369485);
