INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (244048, 'Urokinase', 'Tositumomab', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.  Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.  A complete blood count (CBC) with differential and platelet count should be obtained prior to and at least weekly for a minimum of 10 weeks after treatment.', 'Synergism', 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/286076/', '[1] "Product Information. Bexxar I 131 Therapeutic (iodine I 131 tositumomab)." GlaxoSmithKline  (2022):[2] "Product Information. BexxarTherapy (tositumomab)." GlaxoSmithKline Inc  (2006):', 'Iodide I-131', 'Dicoumarol, Desirudin, Protein C, Acenocoumarol, Selexipag, Anistreplase', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (244049, 'Siponimod', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during siponimod therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.  Siponimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 'The use of live attenuated vaccines should be avoided during and for up to 4 weeks after siponimod treatment, and siponimod should be paused 1 week prior to and until 4 weeks after a planned vaccination.  Patients without a healthcare professional confirmed history of varicella or without documentation of a full course of vaccination against varicella-zoster virus (VZV) should be tested for antibodies to VZV before initiating treatment with siponimod.  For antibody-negative patients, a full course of vaccination with varicella vaccine is recommended prior to treatment with siponimod.  In patients who have recently been vaccinated, siponimod therapy should be postponed for 4 weeks to allow the full effect of vaccination to occur.', 'Synergism', 'The use of live attenuated vaccines should be avoided during and for up to 4 weeks after siponimod treatment, and siponimod should be paused 1 week prior to and until 4 weeks after a planned vaccination.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/286077/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals  (2019):', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', NUL