INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250034, 'Droxidopa', 'Levonordefrin', 'Moderate', 'Agents that increase blood pressure such as vasopressors, triptans, decongestants, beta-2 agonists, and amphetamines may potentiate the risk of supine hypertension associated with the use of droxidopa in the treatment of neurogenic orthostatic hypotension.', 'DDInter', 'Supine blood pressure should be monitored prior to and during droxidopa treatment, and more frequently when increasing dosage.  Patients should elevate the head of the bed when resting or sleeping, and have blood pressure also measured in this position.  Dosage reduction or discontinuation of droxidopa is recommended if supine hypertension cannot be managed by elevation of the head of the bed.  Supine hypertension that is not well managed may increase the risk of cardiovascular events, particularly stroke.', 'Synergism', 'Supine blood pressure should be monitored prior to and during droxidopa treatment, and more frequently when increasing dosage.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296215/', '[1] "Product Information. Northera (droxidopa)." Chelsea Therapeutics Inc  (2014):', 'Dobutamine, Norepinephrine, Amrinone, Angiotensin II, Ephedrine, Milrinone, Arbutamine, Phenylephrine, Fenoldopam, Metaraminol, Epinephrine, More', NULL, 1767369485);
