INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250051, 'Levonordefrin', 'Levomilnacipran', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors.', 'DDInter', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.  Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter.  Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296233/', '[1] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories  (2001):[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company  (2004):[3] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories  (2008):[4] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals  (2009):[5] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals  (2009):[6] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals  (2013):', 'Citalopram, Sertraline, Paroxetine, Maprotiline, Fluoxetine, St. John''s Wort, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Vortioxetine, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250052, 'Linezolid', 'Levonordefrin', 'Major', 'Linezolid may potentiate the pressor response to sympathomimetic agents.  Linezolid is a reversible, nonselective monoamine oxidase inhibitor (MAOI) and, as such, may enhance sympathomimetic effect by increasing norepinephrine storage in adrenergic neurons.  The interaction may be more likely to occur with indirect- or mixed-acting sympathomimetics such as pseudoephedrine or ephedrine than with direct-acting agents like epinephrine, norepinephrine, and isoproterenol.', 'DDInter', 'Unless blood pressure and clinical status can be closely monitored, linezolid should not be administered in combination with direct- or indirect-acting sympathomimetic agents (e.g., pseudoephedrine, phenylpropanolamine), vasopressive agents (e.g., epinephrine, norepinephrine), or dopaminergic agents (e.g., dopamine, dobutamine).  If the combination is used, lower initial dosages of adrenergic agents such as dopamine or epinephrine are recommended, with careful titration to the desired response.', 'Synergism', 'Unless blood pressure and clinical status can be closely monitored, linezolid should not be administered in combination with direct- or indirect-acting sympathomimetic agents (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296235/', '[1] "Product Information. Zyvox (linezolid)." Pharmacia and Upjohn  (2001):[2] Hendershot PE, Antal EJ, Welshman IR, Batts DH, Hopkins NK "Linezolid: pharmacokinetic and pharmacodynamic evaluation of coadministration with pseudoephedrine HCl, phenylpropanolamine HCl, and dextromethorpan HBr." J Clin Pharmacol 41 (2001):  563-72', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Daptomycin, Vancomycin, Metronidazole, Telavancin, Methenamine, Spectinomycin, Lefamulin, More', NULL, 1767369485);
