INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250701, 'Colchicine', 'Radium Ra 223 dichloride', 'Moderate', 'The concomitant use of bone marrow depressants and radium Ra 223 dichloride may have additive myelosuppressive effects.', 'DDInter', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.  Dose reductions of the other bone marrow depressants may be necessary.  Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.  The safety and efficacy of concomitant use of chemotherapy have not been established.  Outside of a clinical trial, concomitant use with chemotherapy is not recommended due to the potential for additive myelosuppression.  If chemotherapy, other systemic radioisotopes or hemibody external radiotherapy are administered during the treatment period, radium Ra 223 should be discontinued.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296956/', '[1] "Product Information. Xofigo (radium Ra 223 dichloride)." Bayer Pharmaceutical Inc  (2022):', 'Lesinurad, Febuxostat, Pegloticase, Sulfinpyrazone, Rasburicase, Probenecid, Allopurinol', NULL, 1767369485);
