INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251085, 'Dalfopristin', 'Alfuzosin', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of alfuzosin, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if alfuzosin is prescribed in combination with moderate CYP450 3A4 inhibitors.  Pharmacologic response to alfuzosin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the alfuzosin dosage adjusted as necessary.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, orthostasis, syncope, or tachycardia.  Taking alfuzosin at bedtime may minimize the occurrence of orthostatic effects.  Patients should also avoid driving or operating hazardous machinery until they know how the medication affects them.', 'Metabolism', 'Caution is advised if alfuzosin is prescribed in combination with moderate CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297403/', '[1] "Product Information. Uroxatral (alfuzosin)." sanofi-aventis  (2003):', 'Finasteride, Terazosin', NULL, 1767369485);
