INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251097, 'Dalfopristin', 'Atazanavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of atazanavir, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone.  Pharmacologic response to atazanavir should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.  In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with atazanavir.', 'Metabolism', 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297416/', '[1] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb  (2003):', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', NULL, 1767369485);
