INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251122, 'Dalfopristin', 'Cevimeline', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of cevimeline, which is metabolized by these isoenzymes.', 'DDInter', 'Dosage adjustments and clinical monitoring may be appropriate whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy.  Patients should be advised to notify their physician if they experience excessive adverse cholinergic effects of cevimeline such as nausea, vomiting, diarrhea, sweating, salivation, urinary frequency, visual disturbance, confusion, tremor, palpitations, or irregular heartbeat.', 'Metabolism', 'Dosage adjustments and clinical monitoring may be appropriate whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297445/', '[1] "Product Information. Evoxac (cevimeline)." Daiichi Pharmaceuticals  (2001):', 'Bethanechol, Neostigmine, Pyridostigmine, Ambenonium, Pilocarpine', NULL, 1767369485);
