INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251127, 'Dalfopristin', 'Cisapride', 'Major', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme, including certain agents that can cause prolongation of the QTc interval (e.g., cisapride, disopyramide, pimozide, quinidine, tacrolimus).', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297450/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 'Metoclopramide', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251128, 'Dalfopristin', 'Citalopram', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', 'DDInter', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.  Administration of racemic citalopram (40 mg) in combination with the potent CYP450 3A4 inhibitor ketoconazole (200 mg) decreased the ketoconazole peak plasma concentration (Cmax) by 21% and systemic exposure (AUC) by 10%, but did not significantly affect the pharmacokinetics of citalopram.  Likewise, coadministration of a single 20 mg dose of escitalopram with a single 600 mg dose of ritonavir, another potent CYP450 3A4 inhibitor, did not affect the pharmacokinetics of either drug.  Because citalopram and escitalopram are both metabolized by multiple isoenzymes (CYP450 2C19, 3A4, and 2D6), inhibition of a single isoenzyme may not appreciably decrease their clearance.', 'Metabolism', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297451/', '[1] "Product Information. Celexa (citalopram)." Forest Pharmaceuticals  (2001):[2] "Product Information. Lexapro (escitalopram)." Forest Pharmaceuticals  (2002):', 'Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, 