INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251158, 'Doravirine', 'Dalfopristin', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.  In 10 study subjects, administration of a single 50 mg dose of doravirine with the potent CYP450 3A4 inhibitor ritonavir (100 mg twice daily) increased doravirine peak plasma concentration (Cmax), systemic exposure (AUC) and trough plasma concentration (C24hr) by an average of 31%, 254% and 191%, respectively, compared to administration of doravirine alone.  When a single 100 mg dose of doravirine was administered with ketoconazole 400 mg once daily in 10 study subjects, doravirine Cmax, AUC and C24hr increased by an average of 25%, 206% and 175%, respectively.  These changes are not considered clinically significant.', 'Metabolism', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297483/', '[1] "Product Information. Pifeltro (doravirine)." Merck & Co., Inc  (2018):', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', NULL, 1767369485);
