INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251160, 'Dronedarone', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dronedarone, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if dronedarone is prescribed in combination with CYP450 3A4 inhibitors.  Pharmacologic response to dronedarone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.  Patients should be advised to seek medical attention if they experience symptoms that could indicate dronedarone toxicity such as excessive fatigue, fainting, difficulty breathing, chest pain or tightness, bradycardia, or symptoms of torsade de pointes such as dizziness, palpitations, and syncope.  Dronedarone should be discontinued if QTc Bazett interval increases to 500 ms or greater.', 'Metabolism', 'Caution is advised if dronedarone is prescribed in combination with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297485/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis  (2009):', 'Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251161, 'Drospirenone', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of drospirenone, which is partially metabolized by the isoenzyme.', 'DDInter', 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.g., renal insufficiency; coadministration with medications that can increase potassium such as aldosterone antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium-sparing diuretics, potassium supplements, and heparin).  Consider checking serum potassium levels during the first treatment cycle and periodically thereafter as indicated.', 'Metabolism', 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297486/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Slynd (drospirenone)." Exeltis USA, Inc.  (2022):[3] "Product Information. Angeliq (drospirenone-estradiol)." Bayer Pharmaceutical Inc  (2017):[4] "Product Information. Slinda (drospirenone)." Besins Healthcare Australia Pty Ltd  (2021):[5] "Product Information. Angeliq 1/2 (drospirenone-estradiol)." Bayer Australia Limited  (2021):[6] "Product Information. Slynd (drospirenone)." Duchesnay Inc  (2021):[7] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb  (2022):[8] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Australia Pty Ltd  (2023):[9] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Pharmaceuticals Ltd  (2021):', 'Norelgestromin, Megestrol acetate, Norgestimate, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Estetrol, Etonogestrel, Norgestrel, Megestrol acetate, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251162, 'Duvelisib', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of duvelisib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised when duvelisib is used with CYP450 3A4 inhibitors.  Patients should be monitored for the development of toxicities such as diarrhea, colitis, neutropenia, thrombocytopenia, pneumonitis, infections, cutaneous reactions and hepatotoxicity, and the dosing adjusted or discontinued in accordance with the duvelisib product labeling.', 'Metabolism', 'Caution is advised when duvelisib is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297488/', '[1] "Product Information. Copiktra (duvelisib)." Verastem, Inc.  (2018):[2] "Product Information. Copiktra (duvelisib)." Verastem, Inc.  (2021):', 'Dabrafenib, Dacomitinib, Vemurafenib, Vandetanib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erlotinib, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251163, 'Efavirenz', 'Dalfopristin', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', 'DDInter', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297489/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', NULL, 1767369485);
