INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251174, 'Erythromycin', 'Dalfopristin', 'Major', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme, including certain agents that can cause prolongation of the QTc interval (e.g., cisapride, disopyramide, pimozide, quinidine, tacrolimus).', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297501/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 'Clindamycin, Telithromycin, Dirithromycin, Azithromycin, Lincomycin, Minocycline, Clindamycin, Vitamin A, Isotretinoin, Chloramphenicol, Fluorometholone, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251175, 'Escitalopram', 'Dalfopristin', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', 'DDInter', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.  Administration of racemic citalopram (40 mg) in combination with the potent CYP450 3A4 inhibitor ketoconazole (200 mg) decreased the ketoconazole peak plasma concentration (Cmax) by 21% and systemic exposure (AUC) by 10%, but did not significantly affect the pharmacokinetics of citalopram.  Likewise, coadministration of a single 20 mg dose of escitalopram with a single 600 mg dose of ritonavir, another potent CYP450 3A4 inhibitor, did not affect the pharmacokinetics of either drug.  Because citalopram and escitalopram are both metabolized by multiple isoenzymes (CYP450 2C19, 3A4, and 2D6), inhibition of a single isoenzyme may not appreciably decrease their clearance.', 'Metabolism', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297502/', '[1] "Product Information. Celexa (citalopram)." Forest Pharmaceuticals  (2001):[2] "Product Information. Lexapro (escitalopram)." Forest Pharmaceuticals  (2002):', 'Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, Amoxapine, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251176, 'Estazolam', 'Dalfopristin', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', 'DDInter', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297503/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 'Methohexital, Methylphenobarbital, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Butabarbital, Secobarbital, Chloral hydrate, Quazepam, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251177, 'Estradiol', 'Dalfopristin', 'Moderate', 'The effectiveness of estrogen-containing oral contraceptives may be impaired by concomitant treatment with antimicrobial agents.  However, the risk appears to be small, and supportive data are primarily limited to anecdotal evidence from case reports and findings from uncontrolled or poorly controlled studies.  Most antimicrobials, with the exception of the rifamycins and possibly griseofulvin, do not induce hepatic enzymes and have not been shown to significantly increase the clearance of oral contraceptive estrogens.  Some investigators believe that antimicrobials interfere with the enterohepatic recirculation of estrogens by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating parent estrogen molecules following first-pass metabolism.', 'DDInter', 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy.  Alternative or additional methods of birth control should be considered during and for at least one week beyond the last dose of short-term antimicrobial therapy, and for at least the initial weeks of long-term antimicrobial therapy when risk may be the greatest.', 'Others', 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297504/', '[1] Friedman CI, Huneke AL, Kim MH, Powell J "The effect of ampicillin on oral contraceptive effectiveness." Obstet Gynecol