INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251193, 'Fosaprepitant', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.  Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.  In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297522/', '[1] "Product Information. Emend (aprepitant)." Merck & Co., Inc  (2003):[2] "Product Information. Emend for Injection (fosaprepitant)." Merck & Co., Inc  (2008):', NULL, NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251194, 'Fostamatinib', 'Dalfopristin', 'Moderate', 'Coadministration of fostamatinib with inhibitors of CYP450 3A4 may increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration.  Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9.  In vitro, R406 is also a substrate of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297523/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals  (2018):', 'Eltrombopag, Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Phylloquinone, Lusutrombopag, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, More', NULL, 1767369485);
