INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251198, 'Granisetron', 'Dalfopristin', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', 'DDInter', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297528/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 'Nabilone, Scopolamine, Dronabinol, Rolapitant, Dolasetron, Ondansetron, Palonosetron', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251199, 'Guanfacine', 'Dalfopristin', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of guanfacine, which is primarily metabolized by the isoenzyme.  The risk of adverse reactions such as hypotension, bradycardia, and sedation may increase.', 'DDInter', 'Caution and dosage adjustment are advised when guanfacine is administered with potent and moderate CYP450 3A4 inhibitors.  For extended-release guanfacine, the manufacturers recommend reducing the dosage to half the recommended level during concomitant use .  Further dosage adjustments may be required based on patient tolerance and response.  The dosage should be increased to the recommended level following discontinuation of the CYP450 3A4 inhibitor.', 'Metabolism', 'Caution and dosage adjustment are advised when guanfacine is administered with potent and moderate CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297529/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Intuniv (guanfacine)." Shire US Inc  (2009):', 'Rauwolfia serpentina root, Reserpine, Methyldopa, Clonidine, Deserpidine', NULL, 1767369485);
