INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251219, 'Ivacaftor', 'Dalfopristin', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 'ADJUST DOSE:  Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.  In study subjects, ivacaftor peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 2.5- and 3.0-fold when it was administered concomitantly with fluconazole, a moderate CYP450 3A4 inhibitor.  When lumacaftor/ivacaftor was coadministered with ciprofloxacin, another moderate CYP450 3A4 inhibitor, lumacaftor Cmax and AUC decreased by 12% and 14%, respectively, while ivacaftor Cmax and AUC increased by 29% each.  These changes are not considered clinically significant.  Physiologically based pharmacokinetic (PBPK) simulations suggest that coadministration with moderate CYP450 3A4 inhibitors may increase elexacaftor AUC by 1.9- to 2.3-fold and tezacaftor AUC by approximately 2.1-fold.', 'Metabolism', 'ADJUST DOSE:  Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297550/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals  (2012):[3] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals  (2015):[4] "Product Information. Symdeko (ivacaftor-tezacaftor)." Vertex Pharmaceuticals  (2022):[5] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals  (2019):', 'Nitric Oxide, Doxapram, Elexacaftor, Lumacaftor', NULL, 1767369485);
